import React from 'react';
import { View, Text, StyleSheet, TextInput, Image, ScrollView, TouchableOpacity } from 'react-native';
import Icon from 'react-native-vector-icons/Ionicons';
import { useNavigation } from '@react-navigation/native'; // Import useNavigation

const BookingApp = () => {
  const navigation = useNavigation(); // Initialize useNavigation

  // Array to hold multiple card data
  const homes = [
    {
      id: 1,
      title: 'Kalam Hall',
      image: 'data:image/jpeg;base64,',  // Replace with real image URL
      guests: '100 seats',
      bedrooms: '1 Boardim',
      rating: '4.9 (2170) ',
      price: 'RVS CAS',
    },
    {
      id: 2,
      title: 'C.K.Prahalad HALL',
      image: 'data:image/jpeg;base64,',  // Replace with real image URL
      guests: '80 seats',
      bedrooms: '1 Boardim',
      bedrooms: '2 Screen',
      rating: '4.9 (3500) ',
      price: 'RVS CAS',
    },
    // Add more cards as necessary
  ];

  return (
    <View style={styles.container}>
      {/* Header Section */}
      <View style={styles.header}>
        <Text style={styles.greeting}>RVS CAS Seminar halls</Text>
        <View style={styles.searchBar}>
          <Icon name="search" size={20} color="#fff" />
          <TextInput
            placeholder="Search"
            placeholderTextColor="#fff"
            style={styles.searchInput}
          />
        </View>
      </View>

      {/* Scrollable Section */}
      <ScrollView style={styles.scrollContainer}>
        <Text style={styles.sectionTitle}>The most relevant</Text>
        
        {/* Loop through the homes array to render multiple cards */}
        {homes.map(home => (
          <View key={home.id} style={styles.card}>
            <Image source={{ uri: home.image }} style={styles.cardImage} />
            <View style={styles.cardContent}>
              <Text style={styles.cardTitle}>{home.title}</Text>
              <Text style={styles.cardDetails}>{home.guests} · {home.bedrooms} · {home.bathroom}</Text>
              <View style={styles.cardFooter}>
                <Text style={styles.rating}>{home.rating}</Text>
                <Text style={styles.price}>{home.price}</Text>
              </View>
            </View>
          </View>
        ))}
      </ScrollView>

      {/* Bottom Navigation */}
      <View style={styles.bottomNav}>
        <TouchableOpacity style={styles.navItem} activeOpacity={0.7}>
          <Icon name="home-outline" size={24} color="#000" />
          <Text style={styles.navText}>Discover</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.navItem} activeOpacity={0.7}>
          <Icon name="heart-outline" size={24} color="#aaa" />
          <Text style={styles.navText}>Favorites</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={styles.navItem}
          activeOpacity={0.7}
          onPress={() => navigation.navigate('Bestseller')} // Ensure ADMINHOMESCREEN exists in your navigator
        >
          <Icon name="briefcase-outline" size={24} color="#aaa" />
          <Text style={styles.navText}>Bookings</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.navItem} activeOpacity={0.7}>
          <Icon name="chatbubble-ellipses-outline" size={24} color="#aaa" />
          <Text style={styles.navText}>Messages</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F8F8F8',
  },
  header: {
    backgroundColor: '#911616',
    padding: 20,
    paddingTop: 40,
  },
  greeting: {
    color: '#fff',
    fontSize: 18,
    fontWeight: 'bold',
  },
  searchBar: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    borderRadius: 25,
    marginTop: 15,
    paddingHorizontal: 15,
    paddingVertical: 10,
  },
  searchInput: {
    color: '#fff',
    marginLeft: 10,
    flex: 1,
  },
  scrollContainer: {
    paddingHorizontal: 20,
    marginTop: 20,
  },
  sectionTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  card: {
    backgroundColor: '#fff',
    borderRadius: 15,
    overflow: 'hidden',
    marginBottom: 20,
    shadowColor: '#000',
    shadowOpacity: 0.1,
    shadowRadius: 10,
    elevation: 5,
  },
  cardImage: {
    width: '100%',
    height: 200,
    resizeMode: 'cover',
  },
  cardContent: {
    padding: 15,
  },
  cardTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  cardDetails: {
    fontSize: 14,
    color: '#888',
    marginBottom: 10,
  },
  cardFooter: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  rating: {
    color: '#FFD700', // Gold color for the rating
    fontSize: 14,
  },
  price: {
    fontWeight: 'bold',
    fontSize: 14,
  },
  bottomNav: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',
    paddingVertical: 10,
    backgroundColor: '#fff',
    borderTopColor: '#eee',
    borderTopWidth: 1,
  },
  navItem: {
    alignItems: 'center',
  },
  navText: {
    fontSize: 12,
    color: '#aaa',
  },
});

export default BookingApp;
